@bot.callback_query_handler(func=lambda call: True)
def callback_worker(call):
    if call.data == "zodiac":
        msg = random.choice(first) + ' ' + random.choice(second) + ' ' + random.choice(second_add) + ' ' + random.choice(third) + random.choice(fourth) + ' ' + random.choice(fifth) + ' ' + random.choice(sixth) + ' ' + random.choice(seventh) + ' ' + random.choice(eighth)
        bot.send_message(call.message.chat.id, msg)
    elif call.data == "love":
        bot.send_message(call.message.chat.id, random.choice(textlove1))
    elif call.data == "future":
        bot.send_message(call.message.chat.id, random.choice(textfuture))
